pub fn not_found_page() -> String {
    return String::from("<!DOCTYPE html><html lang=\"en\"><head><meta charset=\"UTF-8\"><meta name=\"viewport\" content=\"width=device-width,initial-scale=1\"><title>404 Not Found</title><style>body{background-color:#f8f9fa;color:#343a40;font-family:Arial,sans-serif;margin:0;display:flex;justify-content:center;align-items:center;height:100vh;text-align:center}.container{max-width:600px;padding:20px}h1{font-size:3em;margin:0}p{font-size:1.2em;margin:20px 0}@media (max-width:600px){h1{font-size:2.5em}p{font-size:1em}}</style></head><body><div class=\"container\"><h1>404</h1><p>Oops! The page you are looking for cannot be found.</p></div></body></html>");
}
